import { readdir, stat } from 'fs/promises';
import { resolve, resolve as resolveFn, join } from 'path';
const getFiles = async (dir) => {
    const files = await readdir(dir);
    const all = await Promise.all(files.map(async (name) => {
        const file = dir + '/' + name;
        const stats = await stat(file);
        return stats && stats.isDirectory()
            ? await getFiles(file)
            : [resolve(dir, file)];
    }));
    return all.flat();
};
export const staticPlugin = async ({ assets = 'public', prefix = '/public', staticLimit = 1024, alwaysStatic = false, ignorePatterns = ['.DS_Store', '.git', '.env'], noExtension = false, resolve = resolveFn } = {
    assets: 'public',
    prefix: '/public',
    staticLimit: 1024,
    alwaysStatic: process.env.NODE_ENV === 'production',
    ignorePatterns: [],
    noExtension: false,
    resolve: resolveFn
}) => {
    const files = await getFiles(resolveFn(assets));
    if (prefix === '/')
        prefix = '';
    const shouldIgnore = (file) => {
        if (!ignorePatterns.length)
            return false;
        return ignorePatterns.find((pattern) => {
            if (typeof pattern === 'string')
                return pattern.includes(file);
            else
                return pattern.test(file);
        });
    };
    return (app) => {
        if (alwaysStatic ||
            (process.env.ENV === 'production' && files.length <= staticLimit))
            for (let i = 0; i < files.length; i++) {
                const file = files[i];
                if (shouldIgnore(file))
                    continue;
                const response = () => new Response(Bun.file(file));
                let fileName = file
                    .replace(resolve(), '')
                    .replace(`${assets}/`, '');
                if (noExtension) {
                    const temp = fileName.split('.');
                    temp.splice(-1);
                    fileName = temp.join('.');
                }
                app.get(join(prefix, fileName), response);
            }
        else {
            if (
            // @ts-ignore
            !app.routes.find(({ method, path }) => path === `${prefix}/*` && method === 'GET'))
                app.get(`${prefix}/*`, (c) => {
                    const file = `${assets}/${c.params['*']}`;
                    if (shouldIgnore(file))
                        return new Response('NOT_FOUND', {
                            status: 404
                        });
                    return stat(file)
                        .then((status) => new Response(Bun.file(file)))
                        .catch((error) => new Response('NOT_FOUND', {
                        status: 404
                    }));
                });
        }
        return app;
    };
};
export default staticPlugin;
